// ============ HELPER FUNCTIONS ============
export function getDifficultyLevel(dayNumber, pathway) {
  if (pathway === 'clb5') {
    if (dayNumber <= 35) return 'A1';
    if (dayNumber <= 70) return 'A2';
    return 'B1';
  } else {
    if (dayNumber <= 56) return 'A1';
    if (dayNumber <= 140) return 'A2';
    if (dayNumber <= 252) return 'B1';
    return 'B2';
  }
}

function getDayOffsetInLevel(dayNumber, pathway) {
  if (pathway === 'clb5') {
    if (dayNumber <= 35) return dayNumber;
    if (dayNumber <= 70) return dayNumber - 35;
    return dayNumber - 70;
  } else {
    if (dayNumber <= 56) return dayNumber;
    if (dayNumber <= 140) return dayNumber - 56;
    if (dayNumber <= 252) return dayNumber - 140;
    return dayNumber - 252;
  }
}

function getResourceForDay(resources, dayNumber, pathway) {
  const level = getDifficultyLevel(dayNumber, pathway);
  const levelResources = resources[level] || resources['A1'];
  const dayOffset = getDayOffsetInLevel(dayNumber, pathway);
  const index = (dayOffset - 1) % levelResources.length;
  const resource = levelResources[index];
  return { ...resource, level, dayNumber, primaryUrl: resource.specificUrl || resource.url };
}

export function getDailyResources(dayNumber, pathway) {
  const level = getDifficultyLevel(dayNumber, pathway);
  return {
    level,
    dayNumber,
    listening: getResourceForDay(LISTENING_RESOURCES, dayNumber, pathway),
    reading: getResourceForDay(READING_RESOURCES, dayNumber, pathway),
    writing: getResourceForDay(WRITING_EXERCISES, dayNumber, pathway),
    speaking: getResourceForDay(SPEAKING_EXERCISES, dayNumber, pathway)
  };
}

export function getProgressDescription(dayNumber, pathway) {
  const level = getDifficultyLevel(dayNumber, pathway);
  const totalDays = pathway === 'clb5' ? 112 : 336;
  const percent = Math.round((dayNumber / totalDays) * 100);
  const levelDescriptions = {
    'A1': 'Foundation Level - Building basic comprehension',
    'A2': 'Elementary Level - Understanding everyday expressions',
    'B1': 'Intermediate Level - Handling most situations',
    'B2': 'Upper Intermediate Level - Complex texts'
  };
  return {
    currentLevel: level,
    description: levelDescriptions[level],
    percentComplete: percent,
    targetLevel: pathway === 'clb5' ? 'B1 (CLB 5)' : 'B2 (CLB 7)'
  };
}

export function getNextLevelPreview(dayNumber, pathway) {
  const currentLevel = getDifficultyLevel(dayNumber, pathway);
  const nextLevelMap = { 'A1': 'A2', 'A2': 'B1', 'B1': 'B2', 'B2': null };
  const nextLevel = nextLevelMap[currentLevel];
  if (!nextLevel) return null;
  const nextLevelDescriptions = {
    'A2': 'More natural speech, past tense, daily conversations',
    'B1': 'Complex topics, news comprehension, formal registers',
    'B2': 'Native speed, debates, literary texts'
  };
  return { level: nextLevel, description: nextLevelDescriptions[nextLevel] };
}

export function getResourceCounts() {
  return {
    listening: { A1: LISTENING_RESOURCES.A1.length, A2: LISTENING_RESOURCES.A2.length, B1: LISTENING_RESOURCES.B1.length, B2: LISTENING_RESOURCES.B2.length },
    reading: { A1: READING_RESOURCES.A1.length, A2: READING_RESOURCES.A2.length, B1: READING_RESOURCES.B1.length, B2: READING_RESOURCES.B2.length },
    writing: { A1: WRITING_EXERCISES.A1.length, A2: WRITING_EXERCISES.A2.length, B1: WRITING_EXERCISES.B1.length, B2: WRITING_EXERCISES.B2.length },
    speaking: { A1: SPEAKING_EXERCISES.A1.length, A2: SPEAKING_EXERCISES.A2.length, B1: SPEAKING_EXERCISES.B1.length, B2: SPEAKING_EXERCISES.B2.length }
  };
}
